hudson.tasks.Shell.command:
#!/bin/bash -x -e
CHANGELOG=changelog-uboot OUT_PATH=/home/jenkins/build/uboot-${JOB_NAME}
# CONFIGS=("pico-imx6s" "pico-imx6dl" "pico-imx6q" "pico-imx7d" "pico-imx6ul" "edm-cf-imx6" # "edm1-imx6ul-nand_spl" CONFIGS=("pico-imx6_spl" "pico-imx7d_spl" "pico-imx6ul_spl" "edm-cf-imx6_spl" "edm1-imx7d_spl" "tek3-imx6ul_spl" "tek-imx6_spl" "tep1-imx6ul_spl" "tep1-imx7d_spl")
get_board_dir() { case "$1" in *pico-imx8mq*) BRDDIR="pico-imx8mq" ;; *pico-imx8mm*) BRDDIR="pico-imx8mm" ;; *pico-imx6ul*) BRDDIR="pico-imx6ul" ;; *pico-imx6*) BRDDIR="pico-imx6" ;; *pico-imx7d*) BRDDIR="pico-imx7d" ;; *edm-imx6*) BRDDIR="edm-imx6" ;; *edm1-cf-imx6*) BRDDIR="edm1-cf-imx6sx" ;; *edm1-imx6ul*) BRDDIR="edm1-imx6ul-nand" ;; *edm1-imx7d*) BRDDIR="edm1-imx7d" ;; *tek3-imx6ul*) BRDDIR="tek3-imx6ul" ;; *tek-imx6*) BRDDIR="tek-imx6" ;; *tep1-imx6ul*) BRDDIR="tep1-imx6ul" ;; *tep1-imx7d*) BRDDIR="tep1-imx7d" ;; *) BRDDIR="$1" ;; esac }
build_uboot() { # create appropriate output directory mkdir -p ${OUT_PATH}/$1
# build make O=$1 clean make O=$1 distclean make O=$1 $1_defconfig make O=$1 -j10
# copy artifacts if [[ "$1" == *"spl"* ]]; then cp ./$1/SPL ${OUT_PATH}/$1/ cp ./$1/spl/u-boot-spl.bin ${OUT_PATH}/$1/ cp ./$1/u-boot.img ${OUT_PATH}/$1/ else cp ./$1/u-boot.bin ${OUT_PATH}/$1/ cp ./$1/board/technexion/${BRDDIR}/*.cfg.cfgtmp ${OUT_PATH}/$1/ fi cp ./$1/u-boot-nodtb.bin ${OUT_PATH}/$1/ || : cp ./$1/arch/arm/dts/*.dtb ${OUT_PATH}/$1/ || : get_board_dir $1 cp ./board/technexion/${BRDDIR}/*.its ${OUT_PATH}/$1/ || : }
# clear previous files [ -d ${OUT_PATH}/ ] && rm -fr ${OUT_PATH}/* || mkdir -p ${OUT_PATH}
# change log if [ "${GIT_COMMIT}" == "${GIT_PREVIOUS_COMMIT}" ]; then git log --oneline -1 > ./${CHANGELOG} else if git cat-file -e ${GIT_PREVIOUS_COMMIT} 2> /dev/null ; then git log --oneline ${GIT_PREVIOUS_COMMIT}..${GIT_COMMIT} > ./${CHANGELOG} else git log --oneline ${GIT_COMMIT} > ./${CHANGELOG} fi fi cp ./${CHANGELOG} ${OUT_PATH}/
# export variables for arm 32 bits environment . buildenv.sh for CONF in ${CONFIGS[@]}; do build_uboot $CONF done
