hudson.tasks.Shell.command:
#!/bin/bash -x -e
BLDDIR=/home/jenkins/build/rescue-images
ROOTFS_SIZE=$(stat --format %s rescue32/images/rootfs.cpio.xz) KERNEL_SIZE=$(stat --format %s arch/arm/boot/zImage) FDT_SIZE=2097152 # 2MB
CONFIGS=("pico-imx7d_spl") # "pico-imx6_spl") # "pico-imx6ul_spl" "edm-cf-imx6_spl" "edm1-imx7d_spl" "tek3-imx6ul_spl" "tek-imx6_spl" "tep1-imx6ul_spl" "tep1-imx7d_spl" "edm1-cf-imx6sx_spl" "edm1-imx6ul-nand_spl" PICOBOARDS=("hobbit" "pi" "nymph" "dwarf") EDMBOARDS=("fairy" "tc0700" "tc1000" "goblin" "gnome")
# clear and make /home/jenkins/build/images/ directory to store disk images [ -d $BLDDIR ] && rm -fr ${BLDDIR}/* || mkdir -p ${BLDDIR}
# build rescue system disk images mkdir -p ./fat
set_uenv() { # update the uEnv.txt file # echo "rescuefile=tnrescue.itb" > ./fat/uEnv.txt # for boot.scr echo "baseboard=${2}" >> ./fat/uEnv.txt # echo "wifi_module=qca" >> ./fat/uEnv.txt # for qca9377 case ${2} in hobbit) echo "displayinfo=video=mxcfb0:dev=lcd,800x480@60,if=RGB24" >> ./fat/uEnv.txt disp=${2}-lcd800x480 ;; pi) echo "displayinfo=video=mxcfb0:dev=lcd,800x480@60,if=RGB24" >> ./fat/uEnv.txt disp=${2}-lcd800x480 ;; nymph) echo "displayinfo=video=mxcfb0:dev=hdmi,1280x720@60,if=RGB24 fbmem=28M" >> ./fat/uEnv.txt disp=${2}-hdmi1280x720 ;; dwarf) echo "displayinfo=video=mxcfb0:dev=hdmi,1280x720@60,if=RGB24 fbmem=28M" >> ./fat/uEnv.txt disp=${2}-hdmi1280x720 ;; fairy) disp=fairy-070 ;; tc0700) echo "displayinfo=video=mxcfb0:dev=ldb,1024x600@60,if=RGB24,bpp=32" >> ./fat/uEnv.txt echo "display_autodetect=off" >> ./fat/uEnv.txt disp=toucan-070 ;; tc1000) echo "displayinfo=video=mxcfb0:dev=ldb,1280x800@60,if=RGB24,bpp=32" >> ./fat/uEnv.txt echo "display_autodetect=off" >> ./fat/uEnv.txt disp=toucan-101 ;; goblin) ;; gnome) ;; *) ;; esac
case ${1} in pico-imx7d) # echo "has_m4=0" >> ./fat/uEnv.txt # for boot.scr ;; *) ;; esac }
dup_image() { # remove existing duplicated image rm -f tn_${1}-${2}_rescue # copy out the xz disk image cp rescue_img tn_${1}-${2}_rescue # setup the uEnv.txt file # set the loopback device using the partition table LOOPPATH=($(losetup -P -f --show tn_${1}-${2}_rescue)) # mount the fat partition to ./fat directory mount -t vfat ${LOOPPATH}p1 ./fat # setup uEnv.txt display set_uenv ${1} ${2} # umount the fat file system umount ./fat # un-set the loop device losetup -d ${LOOPPATH} # md5 checksum the disk image md5sum tn_${1}-${2}_rescue > tn_${1}-${2}_rescue.md5.txt # xz the image and copy out to external volume xz -9 tn_${1}-${2}_rescue }
set_board() {
case $1 in pico*) for BOARD in ${PICOBOARDS[@]}; do dup_image ${1} ${BOARD} cp tn_${1}-${BOARD}_rescue.xz ${BLDDIR}/tn_${1}-${BOARD}_rescue.xz mkdir -p ${1}/${disp} cp tn_${1}-${BOARD}_rescue.md5.txt ${1}/${disp}/rescue.md5.txt cp tn_${1}-${BOARD}_rescue.xz ${1}/${disp}/rescue.xz done; ;; edm*) for BOARD in ${EDMBOARDS[@]}; do dup_image ${1} ${BOARD} cp tn_${1}-${BOARD}_rescue.xz ${BLDDIR}/tn_${1}-${BOARD}_rescue.xz mkdir -p edm1_cf_imx6all/${disp} cp tn_${1}-${BOARD}_rescue.md5.txt edm1_cf_imx6all/${disp}/rescue.md5.txt cp tn_${1}-${BOARD}_rescue.xz edm1_cf_imx6all/${disp}/rescue.xz done; ;; tep1*) dup_image ${1} "tep1" ;; tek3*) dup_image ${1} "tek3" ;; tek-imx6*) dup_image ${1} "tek3" ;; tek*) dup_image ${1} "tek" ;; *) ;; esac # remove the original disk image for the next config rm -fr rescue_img }
set_image() { # gets the disk image file size UBOOT_SIZE=$(stat --format %s ${1}/u-boot.img) DISK_SIZE=$(((((($ROOTFS_SIZE+$UBOOT_SIZE+$KERNEL_SIZE+$FDT_SIZE)/1048576)/8)+1)*8)) echo "Rootfs: $ROOTFS_SIZE Uboot: $UBOOT_SIZE Kernel: $KERNEL_SIZE, Disk: $DISK_SIZE"
# create an empty image file dd if=/dev/zero of=rescue_img bs=1M count=${DISK_SIZE} # fdisk the partitions in the empty image file echo "start=2048, type=c, bootable" | sfdisk rescue_img # set the loopback device using the partition table LOOPPATH=($(losetup -P -f --show rescue_img)) sync # make the file system mkfs.vfat ${LOOPPATH}p1 # mount the fat partition to ./fat directory mount -t vfat ${LOOPPATH}p1 ./fat
# make the fit folder for generating FIT image mkdir -p ./fit/dtb # copy back the root file system image cp rescue32/images/rootfs.cpio.xz ./fit # copy back the kernel image cp arch/arm/boot/zImage ./fit # copy back the device trees cp arch/arm/boot/dts/*.dtb ./fit/dtb # copy back the fit source file cp board/technexion/${1/_spl/}/${2} ./fit/tnrescue.its # generate the fit blob (require u-boot-tools) to ./fat mkimage -f ./fit/tnrescue.its ./fat/tnrescue.itb # clean up fit folder rm -fr ./fit
# copy out the boot.scr.uimg file to ./fat cp ${1}/u-boot.img ./fat # copy out the u-boot.img file to ./fat # cp boot.scr.uimg ./fat # boot.scr # copy out the changelogs cp changelog* ./fat || : # umount the fat file system umount ./fat # dd the SPL to image file dd if=${1}/SPL of=${LOOPPATH} bs=512 seek=2 oflag=dsync conv=notrunc sync # un-set the loop device losetup -d ${LOOPPATH} }
for CONF in ${CONFIGS[@]}; do if [ "$CONF" == "pico-imx6_spl" ]; then set_image ${CONF} tnrescue-imx6dl.its set_board pico-imx6dl set_image ${CONF} tnrescue-imx6q.its set_board pico-imx6q elif [ "$CONF" == "edm-cf-imx6_spl" ]; then set_image ${CONF} tnrescue-imx6dl.its set_board edm-cf-imx6dl set_image ${CONF} tnrescue-imx6q.its set_board edm-cf-imx6q elif [ "$CONF" == "pico-imx7d_spl" ]; then set_image ${CONF} tnrescue.its set_board ${CONF/d_spl/} else set_image ${CONF} tnrescue.its set_board ${CONF/_spl/} fi done
rm -fr ./fat
