hudson.tasks.Shell.command:
#!/bin/bash -x -e
BLDDIR=/home/jenkins/build/rescue-images
CONFIGS=("pico-imx6_spl" "pico-imx7d_spl" "pico-imx6ul_spl" "edm-cf-imx6_spl" "edm1-imx7d_spl" "tek3-imx6ul_spl" "tek-imx6_spl" "tep1-imx6ul_spl" "tep1-imx7d_spl") # "edm1-cf-imx6sx_spl" "edm1-imx6ul-nand_spl" PICOBOARDS=("hobbit" "pi" "nymph" "dwarf") EDMBOARDS=("fairy" "tc0700" "tc1000" "goblin" "gnome")
# clear and make /home/jenkins/build/images/ directory to store disk images [ -d $BLDDIR ] && rm -fr ${BLDDIR}/* || mkdir -p ${BLDDIR}
# build rescue system disk images mkdir -p ./fat
set_uenv_display() { case ${1} in hobbit) echo "displayinfo=video=mxcfb0:dev=lcd,800x480@60,if=RGB24" >> ./fat/uEnv.txt ;; pi) echo "displayinfo=video=mxcfb0:dev=lcd,800x480@60,if=RGB24" >> ./fat/uEnv.txt ;; nymph) echo "displayinfo=video=mxcfb0:dev=hdmi,1280x720@60,if=RGB24 fbmem=28M" >> ./fat/uEnv.txt ;; dwarf) echo "displayinfo=video=mxcfb0:dev=hdmi,1280x720@60,if=RGB24 fbmem=28M" >> ./fat/uEnv.txt ;; fairy) ;; tc0700) echo "displayinfo=video=mxcfb0:dev=ldb,1024x600@60,if=RGB24,bpp=32" >> ./fat/uEnv.txt echo "display_autodetect=off" >> ./fat/uEnv.txt ;; tc1000) echo "displayinfo=video=mxcfb0:dev=ldb,1280x800@60,if=RGB24,bpp=32" >> ./fat/uEnv.txt echo "display_autodetect=off" >> ./fat/uEnv.txt ;; goblin) ;; gnome) ;; *) ;; esac }
set_board() { # copy out the xz disk image cp rescue_img tn_${1}-${2}_rescue # setup the uEnv.txt file # set the loopback device using the partition table LOOPPATH=($(losetup -P -f --show tn_${1}-${2}_rescue)) # mount the fat partition to ./fat directory mount -t vfat ${LOOPPATH}p1 ./fat # update the uEnv.txt file echo "baseboard=${2}" > ./fat/uEnv.txt # setup uEnv.txt display set_uenv_display ${2} # umount the fat file system umount ./fat # un-set the loop device losetup -d ${LOOPPATH} # md5 checksum the disk image md5sum tn_${1}-${2}_rescue > tn_${1}-${2}_rescue.md5.txt # xz the image and copy out to external volume xz -9 tn_${1}-${2}_rescue cp tn_${1}-${2}_rescue.xz ${BLDDIR}/tn_${1}-${2}_rescue.xz }
ROOTFS_SIZE=$(stat --format %s rescue32/images/rootfs.cpio.xz) KERNEL_SIZE=$(stat --format %s arch/arm/boot/zImage) FDT_SIZE=2097152 # 2MB
for CONF in ${CONFIGS[@]}; do # gets the xz file size UBOOT_SIZE=$(stat --format %s ${CONF}/u-boot.img) DISK_SIZE=$(((((($ROOTFS_SIZE+$UBOOT_SIZE+$KERNEL_SIZE+$FDT_SIZE)/1048576)/8)+1)*8)) echo "Rootfs: $ROOTFS_SIZE Uboot: $UBOOT_SIZE Kernel: $KERNEL_SIZE, Disk: $DISK_SIZE"
# create an empty image file dd if=/dev/zero of=rescue_img bs=1M count=${DISK_SIZE} # fdisk the partitions in the empty image file echo "start=2048, type=c, bootable" | sfdisk rescue_img # set the loopback device using the partition table LOOPPATH=($(losetup -P -f --show rescue_img)) sync # make the file system mkfs.vfat ${LOOPPATH}p1 # mount the fat partition to ./fat directory mount -t vfat ${LOOPPATH}p1 ./fat # make the fit folder for generating FIT image mkdir -p ./fit/dtb # copy back the root file system image cp rescue32/images/rootfs.cpio.xz ./fit # copy back the kernel image cp arch/arm/boot/zImage ./fit # copy back the device trees cp arch/arm/boot/dts/*.dtb ./fit/dtb
if [ "$CONF" == "pico-imx6_spl" ] || [ "$CONF" == "edm-cf-imx6_spl" ]; then # copy back the fit source file cp board/technexion/${CONF/_spl/}/tnrescue-imx6dl.its ./fit # generate the fit blob (require u-boot-tools) to ./fat mkimage -f ./fit/tnrescue-imx6dl.its ./fat/tnrescue.itb else # copy back the fit source file cp board/technexion/${CONF/_spl/}/tnrescue.its ./fit # generate the fit blob (require u-boot-tools) to ./fat mkimage -f ./fit/tnrescue.its ./fat/tnrescue.itb fi
# clean up fit folder rm -fr ./fit # copy out the u-boot.img file to ./fat cp ${CONF}/u-boot.img ./fat # umount the fat file system umount ./fat # dd the SPL to image file dd if=${CONF}/SPL of=${LOOPPATH} bs=512 seek=2 oflag=dsync conv=notrunc sync # un-set the loop device losetup -d ${LOOPPATH}
case $CONF in pico*) for BOARD in ${PICOBOARDS[@]}; do set_board ${CONF} ${BOARD} done; ;; edm*) for BOARD in ${EDMBOARDS[@]}; do set_board ${CONF} ${BOARD} done; ;; tep1*) set_board ${CONF} "tep1" ;; tek3*) set_board ${CONF} "tek3" ;; tek-imx6*) set_board ${CONF} "tek3" ;; tek*) set_board ${CONF} "tek" ;; *) ;; esac
# remove the original disk image for the next config rm -fr rescue_img done rm -fr ./fat
