hudson.tasks.Shell.command:
#!/bin/bash -x
set -e
BLDDIR=/home/jenkins/build CONFIGS=("pico-imx6s" "pico-imx6dl" "pico-imx6q" "pico-imx7d" "pico-imx6ul" "edm-cf-imx6" \ "pico-imx6_spl" "pico-imx7d_spl" "pico-imx6ul_spl" "edm-cf-imx6_spl" ) # \ # "edm1-cf-imx6sx_spl" "edm1-imx6ul-nand_spl" "edm1-imx7d_spl" \ # "tek3-imx6ul_spl" "tek-imx6_spl" "tep1-imx6ul_spl" "tep1-imx7d_spl")
get_board_dir() { case "$1" in *pico-imx6ul*) BRD="pico-imx6ul" ;; *pico-imx6*) BRD="pico-imx6" ;; *pico-imx7d*) BRD="pico-imx7d" ;; *pico-imx8m*) BRD="pico-8m" ;; *edm-cf-imx6*) BRD="edm-cf-imx6" ;; *edm1-cf-imx6*) BRD="edm1-cf-imx6sx" ;; *edm1-imx6ul*) BRD="edm1-imx6ul-nand" ;; *edm1-imx7d*) BRD="edm1-imx7d" ;; *tek3-imx6ul*) BRD="tek3-imx6ul" ;; *tek-imx6*) BRD="tek-imx6" ;; *tep1-imx6ul*) BRD="tep1-imx6ul" ;; *tep1-imx7d*) BRD="tep1-imx7d" ;; *) BRD="$1" ;; esac }
# export variables for arm 32 bits environment . buildenv.sh for CONF in ${CONFIGS[@]}; do # clear and make /home/jenkins/build/uboot-rescue/${CONF} directory [ -d ${BLDDIR}/uboot-rescue-old/${CONF} ] && rm -fr ${BLDDIR}/uboot-rescue-old/${CONF}/* || mkdir -p ${BLDDIR}/uboot-rescue-old/${CONF} # change log if [ "${GIT_COMMIT}" == "${GIT_PREVIOUS_COMMIT}" ]; then git log --oneline -1 > ${BLDDIR}/uboot-rescue-old/${CONF}/changelog else git log --oneline ${GIT_PREVIOUS_COMMIT}..${GIT_COMMIT} > ${BLDDIR}/uboot-rescue-old/${CONF}/changelog fi # build make O=BUILD-${CONF} clean make O=BUILD-${CONF} distclean make O=BUILD-${CONF} ${CONF}_defconfig make O=BUILD-${CONF} # copy artifacts get_board_dir $CONF if [[ $CONF == *"spl"* ]]; then cp BUILD-${CONF}/SPL ${BLDDIR}/uboot-rescue-old/${CONF}/SPL cp BUILD-${CONF}/u-boot.img ${BLDDIR}/uboot-rescue-old/${CONF}/u-boot.img else cp BUILD-${CONF}/u-boot.bin ${BLDDIR}/uboot-rescue-old/${CONF}/u-boot.bin cp BUILD-${CONF}/board/technexion/${BRD}/*.cfg.cfgtmp ${BLDDIR}/uboot-rescue-old/${CONF}/imximage.cfg.cfgtmp fi cp board/technexion/${BRD}/tnrescue.its ${BLDDIR}/uboot-rescue-old/${CONF}/tnrescue.its done
