hudson.tasks.Shell.command:
#!/bin/bash -x
#set -e
source sources/meta-fsl-bsp-release/imx/tools/setup-utils.sh
DATE=$(date +%Y%m%d) OUTDIR=/home/jenkins/build/yocto2.5-images CACHEDIR=/home/jenkins/workspace/src/yocto2.5/ccache DLDIR=/home/jenkins/workspace/src/yocto2.5/downloads SSTATEDIR=/home/jenkins/workspace/src/yocto2.5/sstate-cache
# the following settings has to be build. MACHINEs=("pico-imx8mq") # "pico-imx7" "pico-imx6") DISTROs=("fsl-imx-xwayland") # "fsl-imx-wayland" "fsl-imx-wayland" "fsl-imx-x11" "fsl-imx-fb") WLANs=("qca") # "brcm" "all") TGTIMGTYPEs=("fsl-image-qt5-validation-imx") # "fsl-image-validation-imx" "core-image-minimal" "core-image-base" "core-image-sato" "fsl-image-machine-test"
# the followings are uEnv.txt settings BASEBOARD=("pi") # "nymph" "dwarf" "hobbit" DISPLAY=("mipi5") # "lvds7" "hdmi720p" "hdmi1080p" "lcd" "lvds7_hdmi720p" "custom" "mipi5" "hdmi"
# Sets the META_FSL_BSP_RELEASE folder META_FSL_BSP_RELEASE="${WORKSPACE}/sources/meta-fsl-bsp-release/imx/meta-bsp"
# clear output directory [ -d ${OUTDIR} ] || mkdir -p ${OUTDIR}
# clear and make download/sstate-cache/build folder on external volume [ -d ${DLDIR} ] || mkdir -p ${DLDIR} [ -d ${CACHEDIR} ] || mkdir -p ${CACHEDIR} [ -d ${SSTATEDIR} ] || mkdir -p ${SSTATEDIR}
# update ./conf/local.conf setting setup_local_conf () { CURDIR=. # On the first script run, backup the conf/local.conf file # Consecutive runs, it restores the backup and changes are appended on this one. # if [ ! -e $CURDIR/conf/local.conf.org ]; then # cp $CURDIR/conf/local.conf $CURDIR/conf/local.conf.org # else # cp $CURDIR/conf/local.conf.org $CURDIR/conf/local.conf # fi
if grep "^BB_NUMBER_THREADS.*" $CURDIR/conf/local.conf > /dev/null ; then sed -e 's|^BB_NUMBER_THREADS.*|BB_NUMBER_THREADS ?= "16"|g' -i $CURDIR/conf/local.conf else echo "BB_NUMBER_THREADS ?= \"16\"" >> $CURDIR/conf/local.conf fi if grep "^DL_DIR.*" $CURDIR/conf/local.conf > /dev/null ; then sed -e 's|^DL_DIR.*|DL_DIR = "'$DLDIR'"|g' -i $CURDIR/conf/local.conf else echo "DL_DIR = \"$DLDIR\"" >> $CURDIR/conf/local.conf fi if grep "^SSTATE_DIR.*" $CURDIR/conf/local.conf > /dev/null ; then sed -e 's|^SSTATE_DIR.*|SSTATE_DIR = "'$SSTATEDIR'"|g' -i $CURDIR/conf/local.conf else echo "SSTATE_DIR = \"$SSTATEDIR\"" >> $CURDIR/conf/local.conf fi if grep "INHERIT.*ccache.*" $CURDIR/conf/local.conf > /dev/null ; then echo else echo "INHERIT += \"ccache\"" >> $CURDIR/conf/local.conf fi if grep "^CCACHE_DIR.*" $CURDIR/conf/local.conf > /dev/null ; then sed -e 's|%CCACHE_DIR.*|CACHE_DIR = "'$CACHEDIR'"|g' -i $CURDIR/conf/local.conf else echo "CCACHE_DIR = \"$CACHEDIR\"" >> $CURDIR/conf/local.conf fi
echo "CORE_IMAGE_EXTRA_INSTALL += \"chromium-ozone-wayland\"" >> $CURDIR/conf/local.conf
# if [ "$1" == 'qca' ]; then # echo "LICENSE_FLAGS_WHITELIST = \"commercial_qca\"" >> $CURDIR/conf/local.conf # echo "IMAGE_INSTALL_append = \" linux-firmware-qca-tn\"" >> $CURDIR/conf/local.conf # echo Selected wifi firmware: "qca" # elif [ "$1" == 'brcm' ]; then # echo "LICENSE_FLAGS_WHITELIST = \"commercial_brcm\"" >> $CURDIR/conf/local.conf # echo "IMAGE_INSTALL_append = \" linux-firmware-brcm-tn\"" >> $CURDIR/conf/local.conf # echo Selected wifi firmware: "brcm" # elif [ "$1" == "all" ]; then # echo "LICENSE_FLAGS_WHITELIST = \"commercial_qca commercial_brcm\"" >> $CURDIR/conf/local.conf # echo "IMAGE_INSTALL_append ?= \" linux-firmware-qca-tn linux-firmware-brcm-tn\"" >> $CURDIR/conf/local.conf # echo Selected wifi firmware: "qca brcm" # fi
echo ===== ./conf/local.conf ===== cat $CURDIR/conf/local.conf unset CURDIR }
# update ./conf/bblayer.conf setting setup_bblayer_conf () { CURDIR=. # On the first script run, backup the conf/bblayers.conf file # if [ ! -e $CURDIR/conf/bblayers.conf.org ]; then # cp $CURDIR/conf/bblayers.conf $CURDIR/conf/bblayers.conf.org # else # cp $CURDIR/conf/bblayers.conf.org $CURDIR/conf/bblayers.conf # fi # # echo "" >> $CURDIR/conf/bblayers.conf # echo "# i.MX Yocto Project Release layers" >> $CURDIR/conf/bblayers.conf # echo "BBLAYERS += \" \${BSPDIR}/sources/meta-edm-bsp-release \"" >> ${CURDIR}/conf/bblayers.conf # hook_in_layer meta-fsl-bsp-release/imx/meta-bsp # hook_in_layer meta-fsl-bsp-release/imx/meta-sdk # echo "" >> $CURDIR/conf/bblayers.conf # echo "BBLAYERS += \" \${BSPDIR}/sources/meta-browser \"" >> $CURDIR/conf/bblayers.conf # echo "BBLAYERS += \" \${BSPDIR}/sources/meta-openembedded/meta-gnome \"" >> $CURDIR/conf/bblayers.conf # echo "BBLAYERS += \" \${BSPDIR}/sources/meta-openembedded/meta-networking \"" >> $CURDIR/conf/bblayers.conf # echo "BBLAYERS += \" \${BSPDIR}/sources/meta-openembedded/meta-python \"" >> $CURDIR/conf/bblayers.conf # echo "BBLAYERS += \" \${BSPDIR}/sources/meta-openembedded/meta-filesystems \"" >> $CURDIR/conf/bblayers.conf # echo "BBLAYERS += \" \${BSPDIR}/sources/meta-qt5 \"" >> ${CURDIR}/conf/bblayers.conf
# Support integrating community meta-freescale instead of meta-fsl-arm if [ -d ../sources/meta-freescale ]; then echo meta-freescale directory found # Change settings according to environment sed -e "s,meta-fsl-arm\s,meta-freescale ,g" -i $CURDIR/conf/bblayers.conf sed -e "s,\$.BSPDIR./sources/meta-fsl-arm-extra\s,,g" -i $CURDIR/conf/bblayers.conf fi echo ===== ./conf/bblayers.conf ===== cat $CURDIR/conf/bblayers.conf unset CURDIR }
# build the yocto image build_yocto () { # building the yocto with preset config params echo ===== Building SOM:$1 DISTRO:$2 TARGET:$3 WIFI:$4 =====
# go to workspace for next build cd ${WORKSPACE}
# Set up the basic yocto environment # NOTE: setup-environment shell script change build directory to # /home/jenkins/build/src/build/pico-imx8m/fsl-imx-$distro/fsl-image-validation-imx/wifi-y MACHINE=$1 DISTRO=$2 source ./setup-environment build-$1_$2_$3_wifi-$4
# setup ./conf/local.conf if [ ! -e ./conf/local.conf ]; then echo -e "\n ERROR - No local.conf, Run the 'setup-environment' script again\n" return 1 else setup_local_conf $4 fi
# setup ./conf/bblayers.conf if [ ! -e ./conf/bblayers.conf ]; then echo -e "\n ERROR - No bblayers.conf, Run the 'setup-environment' script again\n" return 1 else setup_bblayer_conf fi
# setup uEnv.txt parameters export BASE_BOARD=$BASEBOARD export RF_FIRMWARES=$4 export DISPLAY_INFO=$DISPLAY export BB_ENV_EXTRAWHITE="$BB_ENV_EXTRAWHITE BASE_BOARD DISPLAY_INFO RF_FIRMWARES" echo "$(pwd)"
# Build using bitbake bitbake $3
ls -al ./tmp/deploy/images/$1 # archive target image if [ -f ./tmp/deploy/images/$1/$3-$1.sdcard.bz2 ]; then echo Archive yocto disk image rm -rf $WORKSPACE/*sdcard.bz2 cp ./tmp/deploy/images/$1/$3-$1.sdcard.bz2 $OUTDIR/$1_${BASEBOARD}_yocto-2.5_$4_$DISPLAY-$DATE.sdcard.bz2 cp ./tmp/deploy/images/$1/$3-$1.sdcard.bz2 $WORKSPACE/$1_${BASEBOARD}_yocto-2.5_$4_$DISPLAY-$DATE.sdcard.bz2 fi }
# loop wifis loop_wifis () { for wifi in ${WLANs[*]}; do build_yocto $1 $2 $3 $wifi done }
# loop targets loop_targets () { for target in ${TGTIMGTYPEs[*]}; do loop_wifis $1 $2 $target done }
# loop distros loop_distros () { for distro in ${DISTROs[*]}; do loop_targets $1 $distro done }
# build loops to build all necessary combinations for som in ${MACHINEs[*]}; do loop_distros $som done
