hudson.tasks.Shell.command:
#!/bin/bash -x -e
MANUSCRIPT=imx-4.14.98-2.0.1_patch-virtualization.xml GITSERVER="10.20.30.20" chmod 0600 ~/.ssh/id_rsa ssh-keyscan ${GITSERVER} >> ~/.ssh/known_hosts
# initialize technexion github yocto repository echo ===== Repo init yocto from TechNexion github ===== rm -fR .repo #${HOME}/bin/repo init -u https://github.com/TechNexion/tn-imx-yocto-manifest.git -b sumo_4.14.y_GA-stable-virtualization -m $MANUSCRIPT #sshpass -p '123456' ${HOME}/bin/repo init -u ssh://rdsw@${GITSERVER}/volume1/internal_git/edm/edm-yocto-bsp.git -b sumo-4.14.y_GA -m $MANUSCRIPT ${HOME}/bin/repo init -u ssh://rdsw@${GITSERVER}/volume1/internal_git/edm/tn-imx-yocto-manifest.git -b sumo_4.14.y_GA-next -m $MANUSCRIPT
mkdir -p .repo/local_manifests/ cp local_manifest.xml .repo/local_manifests/ cat .repo/local_manifests/local_manifest.xml
echo ===== Repo sync yocto from TechNexion github ===== rm -fR sources/meta-tn-imx-bsp rm -fR sources/meta-virtualization ${HOME}/bin/repo sync --force-sync
# Update EULA Agreement Acceptance Config and Create the setup-envronment scripts echo ===== Update EULA Agreement Acceptance Config and Create the setup-envronment scripts ===== sed -i ':a;N;$!ba;s/EULA_ACCEPTED=\n/echo "ACCEPT_FSL_EULA = \\"1\\"" >> conf\/local.conf\n/' sources/base/setup-environment
# mapping re-used directory DATE=$(date +%Y%m%d) OUTDIR=/home/jenkins/build/yocto2.5-images CACHEDIR=/home/jenkins/workspace/src/yocto2.5/ccache DLDIR=/home/jenkins/workspace/src/yocto2.5/downloads SSTATEDIR=/home/jenkins/workspace/src/yocto2.5/sstate-cache
# the following settings has to be build. MACHINEs=("pico-imx8mm" "pico-imx8mq") # "pico-imx7" "pico-imx6") DISTROs=("fsl-imx-xwayland") # "fsl-imx-wayland" "fsl-imx-xwayland" "fsl-imx-x11" "fsl-imx-fb") WLANs="qca" #("qca" "brcm" "all") TGTIMGTYPEs=("tn-image-docker-os") #"fsl-image-qt5-validation-imx") # "core-image-minimal" "core-image-base" "core-image-sato" "fsl-image-machine-test"
# the followings are uEnv.txt settings BASEBOARD=("pi") # "nymph" "dwarf" "hobbit" DISPLAY=("mipi5") # "lvds7" "hdmi720p" "hdmi1080p" "lcd" "lvds7_hdmi720p" "custom" "mipi5" "hdmi"
# Sets the META_FSL_BSP_RELEASE folder META_FSL_BSP_RELEASE="${WORKSPACE}/sources/meta-fsl-bsp-release/imx/meta-bsp"
# clear output directory [ -d ${OUTDIR} ] || mkdir -p ${OUTDIR}
# clear and make download/sstate-cache/build folder on external volume [ -d ${DLDIR} ] || mkdir -p ${DLDIR} [ -d ${CACHEDIR} ] || mkdir -p ${CACHEDIR} [ -d ${SSTATEDIR} ] || mkdir -p ${SSTATEDIR}
# setup display resolutions setup_resolution () { if [ "$1" = "mipi5" ]; then RESO=dsi720x1280 elif [ "$1" = "hdmi" ]; then RESO=hdmi1920x1080 elif [ "$1" = "lcd" ]; then RESO=lcd800x480 fi; }
# update ./conf/local.conf setting setup_local_conf () { CURDIR=.
if grep "^DL_DIR.*" $CURDIR/conf/local.conf > /dev/null ; then sed -e 's|^DL_DIR.*|DL_DIR = "'$DLDIR'"|g' -i $CURDIR/conf/local.conf else echo "DL_DIR = \"$DLDIR\"" >> $CURDIR/conf/local.conf fi if grep "^SSTATE_DIR.*" $CURDIR/conf/local.conf > /dev/null ; then sed -e 's|^SSTATE_DIR.*|SSTATE_DIR = "'$SSTATEDIR'"|g' -i $CURDIR/conf/local.conf else echo "SSTATE_DIR = \"$SSTATEDIR\"" >> $CURDIR/conf/local.conf fi
sed -e 's|^NFC.*|NFC = ""|g' -i $CURDIR/conf/local.conf
# important, must pass JENKINS_HOMEDIR for container-os build echo "DOCKER_SHAREDIR = \"/home/admin/jenkins_home\"" >> $CURDIR/conf/local.conf echo ===== ./conf/local.conf ===== cat $CURDIR/conf/local.conf unset CURDIR }
# update ./conf/bblayer.conf setting setup_bblayer_conf () { CURDIR=.
# Support integrating community meta-freescale instead of meta-fsl-arm if [ -d ../sources/meta-freescale ]; then echo "meta-freescale directory found" # Change settings according to environment sed -e "s,meta-fsl-arm\s,meta-freescale ,g" -i $CURDIR/conf/bblayers.conf sed -e "s,\$.BSPDIR./sources/meta-fsl-arm-extra\s,,g" -i $CURDIR/conf/bblayers.conf fi echo ===== ./conf/bblayers.conf ===== cat $CURDIR/conf/bblayers.conf unset CURDIR }
# build the yocto image build_yocto () { # building the yocto with preset config params echo ===== Building SOM:$1 DISTRO:$2 TARGET:$3 WIFI:$WLANs =====
# go to workspace for next build cd ${WORKSPACE}
rm -fR build-$1
# Set up the basic yocto environment # NOTE: setup-environment shell script change build directory to # /home/jenkins/build/src/build/pico-imx8m/fsl-imx-$distro/fsl-image-validation-imx/wifi-y MACHINE=$1 DISTRO=$2 source ./edm-setup-release.sh -b build-$1 || :
# setup ./conf/local.conf if [ ! -e ./conf/local.conf ]; then echo -e "\n ERROR - No local.conf, Run the 'setup-environment' script again\n" return 1 else setup_local_conf fi
# setup ./conf/bblayers.conf if [ ! -e ./conf/bblayers.conf ]; then echo -e "\n ERROR - No bblayers.conf, Run the 'setup-environment' script again\n" return 1 else setup_bblayer_conf fi
# setup uEnv.txt parameters export BASE_BOARD=$BASEBOARD export RF_FIRMWARES=$WLANs export DISPLAY_INFO=$DISPLAY export DISPLAY_PANEL=ili9881c export BB_ENV_EXTRAWHITE="$BB_ENV_EXTRAWHITE BASE_BOARD DISPLAY_INFO DISPLAY_PANEL RF_FIRMWARES" export
# copy the site.conf to get qca firmware from internal git cp ${WORKSPACE}/site.conf ./conf/site.conf
# Build using bitbake #bitbake multiconfig:container:tn-container-image-lighttpd -c cleanall #bitbake $3 -c cleanall
# must build multiconfig before building the tn-image-docker-os for yocto thud and earlier version as multiconfig dependency not supported bitbake multiconfig:container:tn-container-image-lighttpd bitbake $3
# ls -al ./tmp/deploy/images/$1 # # archive target image # if [ -f ./tmp/deploy/images/$1/$3-$1.sdcard.xz ]; then # cp ./tmp/deploy/images/$1/$3-$1.sdcard.xz $OUTDIR/$1_${BASEBOARD}_yocto-2.5_$DISPLAY_$DATE.sdcard.xz # # figure out where to put image files # setup_resolution $DISPLAY # echo Archive yocto disk image # rm -rf $WORKSPACE/$1/*sdcard.* # mkdir -p $WORKSPACE/$1/$BASEBOARD-$RESO # cp ./tmp/deploy/images/$1/$3-$1.sdcard.xz $WORKSPACE/$1/$1_${BASEBOARD}_yocto-2.5_$DISPLAY_$DATE.sdcard.xz # cp ./tmp/deploy/images/$1/$3-$1.sdcard.xz $WORKSPACE/$1/$BASEBOARD-$RESO/yocto-2.5.xz # fi cp -fR ./tmp/deploy ${WORKSPACE} }
# loop targets loop_targets () { for target in ${TGTIMGTYPEs[*]}; do build_yocto $1 $2 $target done }
# loop distros loop_distros () { for distro in ${DISTROs[*]}; do loop_targets $1 $distro done }
# build loops to build all necessary combinations for som in ${MACHINEs[*]}; do loop_distros $som done
